<style lang="scss">
  *{
    padding: 0;
    margin: 0;
    box-sizing: border-box;
  }
  a{
    text-decoration: none !important;
  }
  .button{
    width: 354px;
    height: 80px;
    background-color: transparent;
    border-radius: 40px;
    font-family: Gilroy;
    font-style: normal;
    font-weight: 900;
    font-size: 20px;
    line-height: 24px;
    text-transform: uppercase;
    transition: all 0.3s;
    border: 4px solid #fff;
    color: #fff;
    &__icon{
      display: inline-block;
      margin-left: 47px;
    }
    &_green{
      background: #55B570;
      color: #fff;
      border: 4px solid #55B570;
      &:hover{
        box-shadow: 0px 10px 20px rgba(85, 181, 112, 0.35);
      }
    }
    &_outline-green{
      border: 4px solid #2E9ADB;
      color: #2E9ADB;
    }
    &_blue{
      background: #2E9ADB;
      color: #fff;
      &:hover{
        box-shadow: 0px 10px 20px rgba(78, 170, 224, 0.35);
      }
    }
  }
  @media screen and (max-width: 768px){
    .button{
      width: 285px;
    }
  }
</style>
